import React, { useState, useMemo, useRef, useCallback } from 'react';
import { sankey, sankeyJustify, sankeyLinkHorizontal } from 'd3-sankey';
import { scaleOrdinal } from 'd3-scale';
import { ParsedFinancialData } from '../types/finance';
import { formatCurrency } from '../utils/csvParser';

interface SankeyChartProps {
  data: ParsedFinancialData;
}

interface SankeyNode {
  name: string;
  category: string;
}

interface SankeyLink {
  source: string;
  target: string;
  value: number;
}

interface SankeyData {
  nodes: SankeyNode[];
  links: SankeyLink[];
}

const SankeyChart: React.FC<SankeyChartProps> = ({ data }) => {
  const [hoveredNode, setHoveredNode] = useState<string | null>(null);
  const [hoveredLink, setHoveredLink] = useState<number | null>(null);
  const [zoom, setZoom] = useState(1);
  const [pan, setPan] = useState({ x: 0, y: 0 });
  const [isDragging, setIsDragging] = useState(false);
  const [dragStart, setDragStart] = useState({ x: 0, y: 0 });
  const svgRef = useRef<SVGSVGElement>(null);

  // Generate Sankey data from financial data
  const sankeyData = useMemo((): SankeyData => {
    const nodes: SankeyNode[] = [];
    const links: SankeyLink[] = [];

    // Add income node
    nodes.push({
      name: 'Monthly Income',
      category: 'income'
    });

    // Add expense nodes and links
    data.expenses.forEach((expense) => {
      const categoryKey = expense.category.toLowerCase().replace(/[^a-z]/g, '');

      nodes.push({
        name: expense.category,
        category: categoryKey
      });

      links.push({
        source: 'Monthly Income',
        target: expense.category,
        value: expense.amount
      });
    });

    // Add savings node and link
    if (data.savings > 0) {
      nodes.push({
        name: 'Savings',
        category: 'savings'
      });

      links.push({
        source: 'Monthly Income',
        target: 'Savings',
        value: data.savings
      });
    }

    return { nodes, links };
  }, [data]);

  // Calculate layout dimensions - make responsive
  const containerWidth = 900;
  const nodeWidth = 25;
  const nodeSpacing = 15;

  // Calculate proportional scaling - income bar should equal sum of all outgoing flows
  const totalOutgoing = data.expenses.reduce((sum, expense) => sum + expense.amount, 0) + data.savings;
  const maxAmount = Math.max(data.monthlyNetIncome, totalOutgoing);
  const minNodeHeight = 20;
  const maxNodeHeight = 80;
  const scaleFactor = (maxNodeHeight - minNodeHeight) / maxAmount;

  // Calculate dynamic height based on proportional node sizes
  const rightNodesCount = sankeyData.nodes.filter(node => node.id !== 'income').length;
  const totalNodeHeights = sankeyData.nodes.reduce((sum, node) => {
    const proportionalHeight = Math.max(minNodeHeight, node.value * scaleFactor);
    return sum + proportionalHeight;
  }, 0);
  const totalSpacing = (rightNodesCount - 1) * nodeSpacing;
  const calculatedHeight = Math.max(400, totalNodeHeights + totalSpacing + 100);
  const containerHeight = calculatedHeight;

  // Calculate node positions
  const calculateLayout = () => {
    const leftNodes = sankeyData.nodes.filter(node => node.id === 'income');
    const rightNodes = sankeyData.nodes.filter(node => node.id !== 'income');

    // Position income node on the left - height should equal total outgoing flows
    leftNodes.forEach((node, index) => {
      // Income bar height should equal the sum of all outgoing flows, not the income amount
      const incomeBarHeight = Math.max(minNodeHeight, totalOutgoing * scaleFactor);
      node.x = 80;
      node.y = containerHeight / 2 - incomeBarHeight / 2;
      node.width = nodeWidth;
      node.height = incomeBarHeight;
    });

    // Position expense/savings nodes on the right with proportional heights
    const totalRightNodesHeight = rightNodes.reduce((sum, node) => {
      const proportionalHeight = Math.max(minNodeHeight, node.value * scaleFactor);
      return sum + proportionalHeight;
    }, 0);

    const totalSpacing = (rightNodes.length - 1) * nodeSpacing;
    const startY = Math.max(50, (containerHeight - totalRightNodesHeight - totalSpacing) / 2);

    let currentY = startY;
    rightNodes.forEach((node) => {
      const proportionalHeight = Math.max(minNodeHeight, node.value * scaleFactor);
      node.x = containerWidth - 180;
      node.y = currentY;
      node.width = nodeWidth;
      node.height = proportionalHeight;
      currentY += node.height + nodeSpacing;
    });
  };

  calculateLayout();

  // Pan and zoom handlers
  const handleWheel = useCallback((e: React.WheelEvent) => {
    e.preventDefault();
    const delta = e.deltaY > 0 ? 0.9 : 1.1;
    const newZoom = Math.max(0.1, Math.min(3, zoom * delta));
    setZoom(newZoom);
  }, [zoom]);

  const handleMouseDown = useCallback((e: React.MouseEvent) => {
    if (e.button === 0) { // Left mouse button
      setIsDragging(true);
      setDragStart({ x: e.clientX - pan.x, y: e.clientY - pan.y });
    }
  }, [pan]);

  const handleMouseMove = useCallback((e: React.MouseEvent) => {
    if (isDragging) {
      setPan({
        x: e.clientX - dragStart.x,
        y: e.clientY - dragStart.y
      });
    }
  }, [isDragging, dragStart]);

  const handleMouseUp = useCallback(() => {
    setIsDragging(false);
  }, []);

  const resetView = useCallback(() => {
    setZoom(1);
    setPan({ x: 0, y: 0 });
  }, []);

  // Touch support for mobile
  const handleTouchStart = useCallback((e: React.TouchEvent) => {
    if (e.touches.length === 1) {
      const touch = e.touches[0];
      setIsDragging(true);
      setDragStart({ x: touch.clientX - pan.x, y: touch.clientY - pan.y });
    }
  }, [pan]);

  const handleTouchMove = useCallback((e: React.TouchEvent) => {
    if (isDragging && e.touches.length === 1) {
      const touch = e.touches[0];
      setPan({
        x: touch.clientX - dragStart.x,
        y: touch.clientY - dragStart.y
      });
    }
  }, [isDragging, dragStart]);

  const handleTouchEnd = useCallback(() => {
    setIsDragging(false);
  }, []);

  // Generate SVG path for links
  const generateLinkPath = (link: SankeyLink): string => {
    const sourceNode = sankeyData.nodes.find(n => n.id === link.source);
    const targetNode = sankeyData.nodes.find(n => n.id === link.target);

    if (!sourceNode || !targetNode) return '';

    const sourceX = (sourceNode.x || 0) + (sourceNode.width || 0);
    const sourceY = (sourceNode.y || 0) + (sourceNode.height || 0) / 2;
    const targetX = targetNode.x || 0;
    const targetY = (targetNode.y || 0) + (targetNode.height || 0) / 2;

    const controlPoint1X = sourceX + (targetX - sourceX) * 0.3;
    const controlPoint2X = sourceX + (targetX - sourceX) * 0.7;

    return `M ${sourceX} ${sourceY} C ${controlPoint1X} ${sourceY}, ${controlPoint2X} ${targetY}, ${targetX} ${targetY}`;
  };

  return (
    <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6 relative">
      <div className="mb-6">
        <h3 className="text-lg font-semibold text-gray-900 mb-2">
          Money Flow Visualization
        </h3>
        <p className="text-sm text-gray-600">
          See how your monthly income flows into expenses and savings
        </p>
      </div>

      <div className="relative">
        {/* Controls */}
        <div className="mb-4 flex justify-between items-center">
          <div className="flex space-x-2">
            <button
              onClick={() => setZoom(Math.max(0.1, zoom - 0.1))}
              className="px-3 py-1 text-sm bg-gray-100 hover:bg-gray-200 rounded border"
            >
              Zoom Out
            </button>
            <button
              onClick={() => setZoom(Math.min(3, zoom + 0.1))}
              className="px-3 py-1 text-sm bg-gray-100 hover:bg-gray-200 rounded border"
            >
              Zoom In
            </button>
            <button
              onClick={resetView}
              className="px-3 py-1 text-sm bg-blue-100 hover:bg-blue-200 text-blue-700 rounded border"
            >
              Reset View
            </button>
          </div>
          <div className="text-sm text-gray-600">
            Zoom: {Math.round(zoom * 100)}% | Scroll to zoom, drag to pan
            <span className="ml-4 text-xs">
              Scale: {formatCurrency(totalOutgoing)} = 100% height
            </span>
          </div>
        </div>

        {/* Chart Container */}
        <div 
          className="border border-gray-200 rounded-lg overflow-hidden"
          style={{ height: '600px', cursor: isDragging ? 'grabbing' : 'grab' }}
          onWheel={handleWheel}
          onMouseDown={handleMouseDown}
          onMouseMove={handleMouseMove}
          onMouseUp={handleMouseUp}
          onMouseLeave={handleMouseUp}
          onTouchStart={handleTouchStart}
          onTouchMove={handleTouchMove}
          onTouchEnd={handleTouchEnd}
        >
          <svg
            ref={svgRef}
            width="100%"
            height="100%"
            viewBox={`${-pan.x} ${-pan.y} ${containerWidth / zoom} ${containerHeight / zoom}`}
            style={{ 
              minWidth: containerWidth * zoom, 
              minHeight: containerHeight * zoom,
              transform: `scale(${zoom})`,
              transformOrigin: '0 0'
            }}
          >
          {/* Render links */}
          {sankeyData.links.map((link, index) => {
            // Calculate proportional stroke width based on total outgoing flows
            const minStrokeWidth = 2;
            const maxStrokeWidth = 20;
            const proportionalStrokeWidth = Math.max(minStrokeWidth, (link.value / totalOutgoing) * maxStrokeWidth);

            return (
              <path
                key={index}
                d={generateLinkPath(link)}
                stroke={link.color}
                strokeWidth={proportionalStrokeWidth}
                fill="none"
                opacity={hoveredLink === index ? 1 : 0.7}
                className="transition-all duration-200 cursor-pointer"
                onMouseEnter={() => setHoveredLink(index)}
                onMouseLeave={() => setHoveredLink(null)}
              />
            );
          })}

          {/* Render nodes */}
          {sankeyData.nodes.map((node) => (
            <g key={node.id}>
              <rect
                x={node.x}
                y={node.y}
                width={node.width}
                height={node.height}
                fill={node.color}
                rx={6}
                className="transition-all duration-200 cursor-pointer shadow-sm"
                style={{
                  opacity: hoveredNode === node.id ? 0.9 : 1,
                  transform: hoveredNode === node.id ? 'scale(1.05)' : 'scale(1)',
                  filter: hoveredNode === node.id ? 'drop-shadow(0 4px 8px rgba(0,0,0,0.2))' : 'none'
                }}
                onMouseEnter={() => setHoveredNode(node.id)}
                onMouseLeave={() => setHoveredNode(null)}
              />

              {/* Node labels */}
              <text
                x={node.id === 'income' ? (node.x || 0) - 10 : (node.x || 0) + (node.width || 0) + 10}
                y={(node.y || 0) + (node.height || 0) / 2}
                textAnchor={node.id === 'income' ? 'end' : 'start'}
                dominantBaseline="middle"
                className="text-xs font-medium fill-gray-700"
              >
                {node.name}
              </text>

              {/* Value labels */}
              <text
                x={node.id === 'income' ? (node.x || 0) - 10 : (node.x || 0) + (node.width || 0) + 10}
                y={(node.y || 0) + (node.height || 0) / 2 + 15}
                textAnchor={node.id === 'income' ? 'end' : 'start'}
                dominantBaseline="middle"
                className="text-xs fill-gray-500"
              >
                {formatCurrency(node.value)}
              </text>
            </g>
          ))}
          </svg>
        </div>
      </div>

      {/* Tooltip */}
      {hoveredNode && (
        <div className="absolute bg-gray-900 text-white px-3 py-2 rounded-lg shadow-lg text-sm z-10 pointer-events-none">
          {(() => {
            const node = sankeyData.nodes.find(n => n.id === hoveredNode);
            if (!node) return null;
            return (
              <div>
                <div className="font-semibold">{node.name}</div>
                <div className="text-gray-300">{formatCurrency(node.value)}</div>
                {node.id === 'income' && (
                  <div className="text-xs text-gray-400 mt-1">
                    {data.savingsPercentage.toFixed(1)}% saved
                  </div>
                )}
              </div>
            );
          })()}
        </div>
      )}

      {/* Legend */}
      <div className="mt-6 grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-3">
        {sankeyData.nodes.map((node) => (
          <div key={node.id} className="flex items-center space-x-2">
            <div
              className="w-3 h-3 rounded"
              style={{ backgroundColor: node.color }}
            />
            <span className="text-xs text-gray-600">
              {node.name}: {formatCurrency(node.value)}
            </span>
          </div>
        ))}
      </div>

      {/* Summary Stats */}
      <div className="mt-6 grid grid-cols-1 md:grid-cols-3 gap-4 p-4 bg-gray-50 rounded-lg">
        <div className="text-center">
          <div className="text-2xl font-bold text-green-600">
            {formatCurrency(data.monthlyNetIncome)}
          </div>
          <div className="text-sm text-gray-600">Total Income</div>
        </div>
        <div className="text-center">
          <div className="text-2xl font-bold text-red-600">
            {formatCurrency(data.totalExpenses)}
          </div>
          <div className="text-sm text-gray-600">Total Expenses</div>
        </div>
        <div className="text-center">
          <div className="text-2xl font-bold text-blue-600">
            {formatCurrency(data.savings)}
          </div>
          <div className="text-sm text-gray-600">Savings ({data.savingsPercentage.toFixed(1)}%)</div>
        </div>
      </div>
    </div>
  );
};

export default SankeyChart;
