import React, { useCallback } from 'react';
import { Upload, FileText } from 'lucide-react';
import { parseCSV } from '../utils/csvParser';
import { ParsedFinancialData } from '../types/finance';

interface CSVUploaderProps {
  onDataParsed: (data: ParsedFinancialData) => void;
}

const CSVUploader: React.FC<CSVUploaderProps> = ({ onDataParsed }) => {
  const handleFileUpload = useCallback((event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (e) => {
      const csvContent = e.target?.result as string;
      try {
        const parsedData = parseCSV(csvContent);
        onDataParsed(parsedData);
      } catch (error) {
        console.error('Error parsing CSV:', error);
        alert('Error parsing CSV file. Please check the format.');
      }
    };
    reader.readAsText(file);
  }, [onDataParsed]);

  return (
    <div className="card">
      <div className="flex items-center space-x-3 mb-4">
        <FileText className="h-6 w-6 text-primary-600" />
        <h2 className="text-xl font-semibold text-gray-900">Import Financial Data</h2>
      </div>

      <div className="border-2 border-dashed border-gray-300 rounded-lg p-8 text-center hover:border-primary-400 transition-colors">
        <Upload className="h-12 w-12 text-gray-400 mx-auto mb-4" />
        <div className="space-y-2">
          <p className="text-lg font-medium text-gray-900">Upload your CSV file</p>
          <p className="text-sm text-gray-500">
            Drag and drop your financial data CSV file here, or click to browse
          </p>
        </div>

        <input
          type="file"
          accept=".csv"
          onChange={handleFileUpload}
          className="hidden"
          id="csv-upload"
        />

        <label
          htmlFor="csv-upload"
          className="btn-primary inline-block mt-4 cursor-pointer"
        >
          Choose File
        </label>
      </div>

      <div className="mt-4 text-sm text-gray-600">
        <p className="font-medium mb-2">Supported format:</p>
        <ul className="list-disc list-inside space-y-1 text-xs">
          <li>CSV files with income and expense data</li>
          <li>Date, income, and expense columns</li>
          <li>Currency values in $ format</li>
        </ul>
      </div>
    </div>
  );
};

export default CSVUploader;
